export default function GenerateCaptionButton({
  widgetId,
  selected,
  onSelect,
}: ButtonProps) {
  return (
    <SVG
      // Using the svg component you can embed an svg right into the widget
      hoverStyle={{ opacity: 0.5 }}
      src={selected ? generateCaptionButtonSelected : inpaintingButton}
      onClick={(event) =>
        new Promise(
          () =>
            new Promise(() => {
              createConnector(widgetId, "generate-caption");
              onSelect();
            })
        )
      }
    />
  );
}
